var SpatialSelector = require('globi-spatial-selector');
var test = require('tape');

test('check append error to body single interaction', function (t) {
    t.plan(4);
    var preTestLength = document.body.children.length;
    var spatialSelector = SpatialSelector({});
    t.deepEqual(spatialSelector.opts, {});
    spatialSelector.on('error', function (target) {
        t.ok("no google loaded, expecting error")
        t.equal(target, document.body);
        t.equal(preTestLength, document.body.children.length);
    });
    spatialSelector.appendTo(document.body);
});